package model

import (
	"encoding/json"
	"testing"
)

var example = `{  
   "CertificateDetails":[  
      {  
         "RecordID":"234571894056781356",
         "Name":"Root Certificate 1",
         "PEM":"'-----BEGIN CERTIFICATE----------END CERTIFICATE-----'",
         "TestWebsiteValid":"http://testvalid.com",
         "TestWebsiteRevoked":"http://testrevoked.com",
         "TestWebsiteExpired":"http://testExpired.com"
      },
      {  
         "RecordID":"1343467940567817777",
         "Name":"Root Certificate 2",
         "PEM":"'-----BEGIN CERTIFICATE----------END CERTIFICATE-----'",
         "TestWebsiteValid":"http://testvalid.com",
         "TestWebsiteRevoked":"http://testrevoked.com",
         "TestWebsiteExpired":"http://testExpired.com"
      }
   ]
}`

func TestUnmarshal(t *testing.T) {
	var records CCADBRecords
	err := json.Unmarshal([]byte(example), &records)
	if err != nil {
		t.Fatal(err)
	}
	t.Log(records)
}
